package com.example.demooooo.controller;import com.example.demooooo.dto.UserDto;import com.example.demooooo.model.User;import com.example.demooooo.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.*;import org.springframework.web.servlet.mvc.support.RedirectAttributes;import java.util.List;@Controllerpublic class UserController {    @Autowired    UserService userService;    @RequestMapping(value = "/")    public String login(Model model) {        model.addAttribute("user", new User());        return "login";    }    @RequestMapping(value = "/login")    public String loginn(@ModelAttribute("user") UserDto user, Model model, RedirectAttributes redirectAttributes) {        List<UserDto> listU = userService.getAllUser();        for (UserDto u : listU) {            if (user.getUsername().equals(u.getUsername())                    && user.getPassword().equals(u.getPassword())                    && u.getRoles().equals("ADMIN")) {                    model.addAttribute("user", u);                    redirectAttributes.addFlashAttribute("success", "Đăng nhập thành công");                return "guest";            } else if (user.getUsername().equals(u.getUsername())                    && user.getPassword().equals(u.getPassword())                    && u.getRoles().equals("GUEST")) {                model.addAttribute("user", u);                redirectAttributes.addFlashAttribute("success", "Đăng nhập thành công");                return "guest";            }        }        return "login";    }    @GetMapping("/admin")    public String guest(@RequestParam("id") Integer id, Model model){       UserDto userDto = userService.getUserById(id).get();        if (userDto.getRoles().equals("ADMIN")){            model.addAttribute("user", userDto);            return "admin";        } else{            return "403";        }    }    @GetMapping("/register")    public String register(Model model) {        model.addAttribute("user", new User());        return "register";    }    @PostMapping("/register")    public String doregister(@ModelAttribute("user") UserDto user, RedirectAttributes redirectAttributes) {        boolean check = false;        List<UserDto> listU = userService.getAllUser();        for (UserDto u :                listU) {            if (user.getUsername().equals(u.getUsername())) {                if (user.getRoles().equals(u.getRoles())) {                    check = true;                }            }        }        if (check) {            redirectAttributes.addFlashAttribute("errorMessage", "Tên người dùng đã tồn tại...");            return "redirect:/register";        } else {            redirectAttributes.addFlashAttribute("message", "Đăng ký thành công. ");            userService.saveUser(user);        }        return "redirect:/";    }}