package com.example.demooooo.dto;import lombok.Getter;import lombok.Setter;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.validation.constraints.Min;import javax.validation.constraints.NotEmpty;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;@Getter@Setterpublic class UserDto {	@Id	@GeneratedValue(strategy = GenerationType.IDENTITY)	private int id;	@NotNull(message = "Ten dang nhap khong duoc de null")	@NotEmpty(message = "Ten dang nhap khong duoc de trong")	private String username;	@Size(min = 8, message = "mat khau toi thieu 8 ky tu")	private String password;	private String roles;}