package com.example.demooooo.service;import com.example.demooooo.dto.UserDto;import com.example.demooooo.mapper.UserMapper;import com.example.demooooo.repository.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class UserService {    @Autowired private UserRepository userRepository;    @Autowired private UserMapper userMapper;    public List<UserDto> getAllUser(){        return userMapper.modelsToDtos(userRepository.findAll());    }    public void saveUser(UserDto userDto) {        userRepository.save(userMapper.dtoToModel(userDto));    }    public Optional<UserDto> getUserById(Integer id){        return Optional.of(userMapper.modelToDto(userRepository.findById(id).get()));    }}